import React, { Component } from 'react';
import './App.css';

export class App extends Component {
   constructor(props) {
      super(props)
      this.state = {
         board: Array(9).fill(null),
         player: "X"

      }
   }

   handleClick(index){
      let newBoard = this.state.board;
      if(this.state.board === null){
         newBoard[index] = this.state.player
         this.setState({
            board: newBoard,
            player: this.state.player === "X"
         })
      }
      newBoard[index] = this.state.player

      this.setState({
         board: newBoard,
         player: this.state.player === "X"? "O":"X"
      })
   }

   render() {
      const BoxValue = this.state.board.map
      (
         (boxjumlah, index) => 
         <div className='box' 
         key={index} 
         onClick=
         {
            () => this.handleClick(index)
         }>{boxjumlah}
         </div>
      )
      return (
         <div className='container'>
            <h1> Tic-tac-toe</h1>
            <div className='board'> {BoxValue}

            </div>

         </div>
      )
   }
}

export default App
